import React from "react";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import { makeStyles } from "@material-ui/core";
import swal from "@sweetalert/with-react";

const useStyles = makeStyles({
    button: {
        position: "fixed",
        right: "100px",
        bottom: "40px",
    },
  });

export const AddButton = () => {
    const classes = useStyles();

    const handleClick = () => {
        swal(
            <form action="http://localhost:5001/user" className="pt-5" method="post" id="user" name="user" >     
                    <div class="col-sm-10 mb-2 text-center">
                        <input type="text" name="name" id="name" placeholder="Nombre" className="form-control w-75 p-3" />
                    </div>
                    <div class="col-sm-10 my-2">
                        <input type="number" name="dni" id="dni" placeholder="DNI" className="form-control w-75 p-3" />
                    </div>
                    <div class="col-sm-10 my-2">
                        <input type="text" name="email" id="email" placeholder="E-mail" className="form-control w-75 p-3" />
                    </div>
            </form>, {
                buttons: {
                    cancel: "Cancelar",
                    confirm: "Confirmar"
                }
            }
        ).then((value) => {
            if (value != null){
                var form = document.querySelector('#user');
                form.submit();
            }
        });
    }

    return (
        <div className={classes.button}>
        <Fab onClick={handleClick} color="primary" aria-label="add">
            <AddIcon />
        </Fab>
        </div>
    );
};
