import React, { useState, useEffect } from 'react';
import { Row, Col } from "react-bootstrap";
import { TextField } from "@material-ui/core";
import axios from 'axios';

export const CustomerEdition = (props) => {
    const id = props.location.pathname.split("/")[2];
    const [customer, setCustomer] = useState("");

    useEffect(() => {
        axios.get('http://localhost:5001/user'+id)
            .then((response) => {
                setCustomer(response.data);
            })
            .catch(error => {
                console.log(error);
            })
    })
    
    return (
        <div class="d-flex flex-column p-5">
            <div class="p-2">
                <TextField type="text" name="name" id="nameField" placeholder="Nombre" fullWidth/>
            </div>
            <div class="p-2">
                <TextField type="number" name="dni" id="dniField" placeholder="DNI" fullWidth/>
            </div>
            <div class="p-2">
                <TextField type="text" name="email" id="emailField" placeholder="E-mail" fullWidth/>
            </div>
        </div>
    )
}
