import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Grid, makeStyles } from "@material-ui/core";
import { CustomerCard } from './CustomerCard';
import { AddButton } from './AddButton';

const useStyles = makeStyles(theme => ({
    grid: {}
}))

export const CustomersView = () => {
    const [customers, setCustomers] = useState("");
    const classes = useStyles();

    useEffect(() => {
        axios.get('http://localhost:5001/customers', {"headers": {"token": localStorage.getItem("token")}})
            .then((response) => {
                setCustomers(response.data);
            })
            .catch((error) => {
                console.log(error);
            })
    }, [setCustomers])

    return (
        <Container maxWidth="md">
            <Grid container spacing={4} justify="center">
            { customers ? (
                customers.map((customer) => 
                    <Grid item xs={12} sm={6} md={4} key={customer._id}>
                        <CustomerCard 
                            customer={customer} 
                            delete={() => 
                                axios.delete('http://localhost:5001/customers/'+customer._id, {"headers": {"token": localStorage.getItem("token")}})
                                .then(() => {
                                    axios.get('http://localhost:5001/customers', {"headers": {"token": localStorage.getItem("token")}})
                                        .then((response) => {
                                            setCustomers(response.data);
                                        })
                                        .catch((error) => {
                                            console.log(error);
                                        })
                                }).catch((error) => console.log(error))
                            }
                        />
                    </Grid>
                )
            ) : null }
                <AddButton 
                    click={(data) =>{
                            setCustomers(customers.concat(data));
                        }
                    }
                />
            </Grid>
        </Container>
    )
}
