import React from "react";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import { makeStyles, TextField } from "@material-ui/core";
import swal from "@sweetalert/with-react";
import { Row, Col } from "react-bootstrap";

const useStyles = makeStyles({
    button: {
        position: "fixed",
        right: "100px",
        bottom: "40px",
    },
  });

export const AddButton = () => {
    const classes = useStyles();

    const handleClick = () => {
        swal(
            <div>
                <Row>
                    <Col xs={{ span: 6, offset: 3 }}>
                    <TextField type="text" name="name" id="name" placeholder="Nombre"/>
                    </Col>
                </Row>
                <Row>
                    <TextField type="number" name="dni" id="dni" placeholder="DNI"/>
                </Row>
                <Row>
                    <TextField type="text" name="email" id="email" placeholder="E-mail"/>
                </Row>
            </div>, {
                buttons: {
                    cancel: "Cancelar",
                    confirm: "Confirmar"
                }
            }
        ).then((value) => {
            if (value != null){
                var form = document.querySelector('#user');
                form.submit();
            }
        });
    }

    return (
        <div className={classes.button}>
        <Fab onClick={handleClick} color="primary" aria-label="add">
            <AddIcon />
        </Fab>
        </div>
    );
};
